mapper.select:
SELECT RNUM , DONATE_NUMBER, DONATEWRITE_TITLE, USER_ID, USER_NAME, DONATE_AMOUNT, TO_CHAR(DONATE_DATE, 'YYYY.MM.DD') AS DONATE_DATE FROM( SELECT ROWNUM AS RNUM,DONATE_NUMBER, DONATEWRITE_TITLE, USER_ID, USER_NAME, DONATE_AMOUNT, DONATE_DATE FROM( SELECT D.DONATE_NUMBER, W.DONATEWRITE_TITLE, U.USER_ID, U.USER_NAME, D.DONATE_AMOUNT, DONATE_DATE FROM TBL_DONATE D, TBL_USERS U, TBL_DONATEWRITE W
WHERE D.DONATEWRITE_NUMBER = W.DONATEWRITE_NUMBER AND U.USER_NUMBER = D.USER_NUMBER AND U.USER_NAME LIKE '%' || #{searchVo.keyword} || '%' ORDER BY D.DONATE_NUMBER DESC
WHERE D.DONATEWRITE_NUMBER = W.DONATEWRITE_NUMBER AND U.USER_NUMBER = D.USER_NUMBER AND U.USER_ID LIKE '%' || #{searchVo.keyword} || '%' ORDER BY D.DONATE_NUMBER DESC
WHERE D.DONATEWRITE_NUMBER = W.DONATEWRITE_NUMBER AND U.USER_NUMBER = D.USER_NUMBER AND W.DONATEWRITE_TITLE LIKE '%' || #{searchVo.keyword} || '%' ORDER BY D.DONATE_NUMBER DESC
WHERE D.DONATEWRITE_NUMBER = W.DONATEWRITE_NUMBER AND U.USER_NUMBER = D.USER_NUMBER ORDER BY D.DONATE_NUMBER DESC
)X WHERE
ROWNUM <= #{criteria.page} * #{criteria.amount}
) Y WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT COALESCE(SUM(DONATE_AMOUNT), 0)
FROM TBL_DONATE
WHERE DONATEWRITE_NUMBER = ${donateWriteNumber}
