mapper.select:
SELECT COUNT(DONATEWRITE_NUMBER)
FROM TBL_DONATEWRITE
WHERE DONATEWRITE_STATUS = #{status}

SELECT DONATEWRITE_NUMBER, DONATEWRITE_TITLE, DONATEWRITE_STATUS, DONATEWRITE_DATE, DONATEFILE_NUMBER, DONATEFILE_NAME, DONATEFILE_UPLOAD_PATH, DONATEFILE_UUID FROM ( SELECT ROWNUM RNUM, DONATEWRITE_NUMBER, DONATEWRITE_TITLE, DONATEWRITE_STATUS, DONATEWRITE_DATE, DONATEFILE_NUMBER, DONATEFILE_NAME, DONATEFILE_UPLOAD_PATH, DONATEFILE_UUID FROM (SELECT w.DONATEWRITE_NUMBER, DONATEWRITE_TITLE, DONATEWRITE_STATUS, DONATEWRITE_DATE, DONATEFILE_NUMBER, DONATEFILE_NAME, DONATEFILE_UPLOAD_PATH, DONATEFILE_UUID FROM TBL_DONATEWRITE W JOIN TBL_DONATEFILE F ON W.DONATEWRITE_NUMBER = F.DONATEWRITE_NUMBER WHERE DONATEWRITE_STATUS = #{status} ORDER BY W.DONATEwRITE_NUMBER DESC)
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT USER_NAME
FROM TBL_USERS
WHERE USER_NUMBER = #{userNumber}

SELECT DONATEWRITE_TITLE
FROM TBL_DONATEWRITE
WHERE DONATEWRITE_NUMBER = #{donateWriteNumber}
